import React, { useState, useEffect } from 'react';

function WeatherDetails({ city }) {
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (city) {
      fetch(`http://localhost:3001/weather/${city.name}`)
        .then((response) => {
          if (!response.ok) {
            throw new Error('Weather data could not be loaded');
          }
          return response.json();
        })
        .then((data) => {
          // Assuming that the API response has the structure { current: { condition: {...} } }
          // You may need to adjust based on the actual API response
          setWeather(data);
        })
        .catch((error) => {
          console.error(error);
          setError(error.message);
        });
    }
  }, [city]);

  if (error) {
    return <p>{error}</p>;
  }

  // Ensure weather data is loaded before trying to access it
  if (!weather || !weather.current || !weather.current.condition) {
    return <p>Loading weather...</p>;
  }

  return (
    <div>
      <h3>Weather in {city.label}</h3>
      <p>{weather.current.condition.text}</p>
      <img src={weather.current.condition.icon} alt="Weather icon" />
      <p>Temperature: {weather.current.temp_c}Â°C</p>
      {/* Display more weather details as needed */}
    </div>
  );
}

export default WeatherDetails;
