import React, { useState, useEffect } from 'react';

function WeatherDetails({ city }) {
  const [weather, setWeather] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchWeatherData = async () => {
      try {
        setLoading(true); // Indicate loading start

        // Determine the endpoint to use
        let endpoint = '';
        if (city.lat && city.lon) {
          // Use coordinates if provided
          endpoint = `http://localhost:3001/weather?lat=${city.lat}&lon=${city.lon}`;
        } else {
          // Otherwise, use city name
          endpoint = `http://localhost:3001/weather/by-city?city=${encodeURIComponent(city.label)}`;
        }

        // Fetch weather data
        const weatherResponse = await fetch(endpoint);
        const weatherData = await weatherResponse.json();

        if (!weatherResponse.ok) {
          throw new Error(weatherData.message || 'Failed to load weather data');
        }

        setWeather(weatherData);
      } catch (error) {
        console.error(error);
        setError(error.message);
      } finally {
        setLoading(false); // Reset loading state regardless of outcome
      }
    };

    if (city && city.label) {
      fetchWeatherData();
    }
  }, [city]);

  // Loading state
  if (loading) {
    return <p>Loading weather information...</p>;
  }

  // Error state
  if (error) {
    return <p>Error: {error}</p>;
  }

  // Loaded state
  if (!weather || !weather.current || !weather.current.condition) {
    return <p>Weather data is not available.</p>;
  }

  return (
    <div>
      <h3>Weather in {city.label}</h3>
      <p>{weather.current.condition.text}</p>
      <img src={`https://${weather.current.condition.icon}`} alt="Weather icon" />
      <p>Temperature: {weather.current.temp_c}Â°C</p>
      {/* You can expand here to display more weather details as needed */}
    </div>
  );
}

export default WeatherDetails;
