import React, { useState, useEffect } from 'react';

function WeatherDetails({ city }) {
  const [weather, setWeather] = useState(null);
  const [coords, setCoords] = useState({ lat: null, lon: null });
  const [error, setError] = useState(null);

  // Fetch coordinates for the city if they are not provided
  useEffect(() => {
    const fetchCoordinates = async (cityName) => {
      try {
        const geoResponse = await fetch(`http://localhost:3001/geo/direct?q=${encodeURIComponent(cityName)}`);
        const geoData = await geoResponse.json();
        if (!geoResponse.ok) throw new Error(geoData.message);
        setCoords({ lat: geoData[0].lat, lon: geoData[0].lon });
      } catch (error) {
        console.error('Geocoding error:', error);
        setError('Failed to load coordinates');
      }
    };

    if (city) {
      if (!city.lat || !city.lon) {
        fetchCoordinates(city.label);
      } else {
        setCoords({ lat: city.lat, lon: city.lon });
      }
    }
  }, [city]);

  // Fetch weather using the coordinates
  useEffect(() => {
    if (coords.lat && coords.lon) {
      fetch(`http://localhost:3001/weather?lat=${coords.lat}&lon=${coords.lon}`)
        .then((response) => response.json())
        .then((data) => {
          setWeather(data);
        })
        .catch((error) => {
          console.error('Weather fetching error:', error);
          setError('Failed to load weather data');
        });
    }
  }, [coords]);

  if (error) {
    return <p>{error}</p>;
  }

  if (!weather || !weather.current || !weather.current.condition) {
    return <p>Loading weather...</p>;
  }

  return (
    <div>
      <h3>Weather in {city.label}</h3>
      <p>{weather.current.condition.text}</p>
      <img src={`http://${weather.current.condition.icon}`} alt="Weather icon" />
      <p>Temperature: {weather.current.temp_c}Â°C</p>
      {/* Display more weather details as needed */}
    </div>
  );
}

export default WeatherDetails;
